import React from "react"
import AssetsInfo from './components/AssetsInfo'
import CollectionsInfo from './components/CollectionsInfo'
import "./styles.css"

export default class App extends React.Component {
  constructor(props){
    super(props);
    this.state={
      assets: [],
      path:"",
      masterID:0,  //collectionId from AssetsInfo.js
      defaultMasterId:0
    }
}

  dataFromCollection = (dataFromCollection)=>{
    this.setState({assets:dataFromCollection})
  }

  imgFromMaster = (imgFromMaster)=>{
    this.setState({path:imgFromMaster})
  }

  idFromMaster = (idFromMaster)=>{
    this.setState({masterID:idFromMaster})
  }

  getDefaultMasterId = (defaultMasterId)=> {
    this.setState({defaultMasterId:defaultMasterId})
  }


  render(){
    console.log("IIIIDDDD"+this.state.masterID)
    return(
      <div className="app">
        <CollectionsInfo dataFromCollection={this.dataFromCollection}
          getDefaultMasterId={this.getDefaultMasterId} 
          masterPath={this.state.path} 
          masterID={this.state.masterID} />

        <AssetsInfo selectedAssets={this.state.assets} 
          imgFromMaster={this.imgFromMaster} 
          idFromMaster={this.idFromMaster} />
      </div>
    )
  }
}
