import React, { useState, useEffect } from 'react'

export default function useSortForm (initialData) {
  const [ data, setData ] = useState(initialData) 

  useEffect(() => {
    setData(initialData)
  },[initialData]) 

  //console.dir('initialData: '+ data)

    const setSortAssets = (sortKey)=>{
        const compare_fun=(a, b)=>{
            if(a[sortKey] < b[sortKey]){
                return -1;
            }else if(a[sortKey] > b[sortKey]){
                return 1;
            }else{
                return 0;
            }
        }   
    let t=data.sort(compare_fun)
    //console.dir(t)
    //setData(t)     
    useEffect(() => {
        setData(t)
      })  
    } 

    //console.dir('data returned: '+ data[0])  
  return [data, setSortAssets]   



}
