export default function CollectionsInfo () {
  const [ collections, setCollections ] = useState([])

  useEffect(() => {
    getCollectionsAsync().then((data) => {
      setCollections(data)
      data.forEach(collection => {
        props.setDefaultMasterId(collection.id, collection.masterAssetId)
      })
    })
  })

  const getRecurName = (tag) => {
    if('subTag' in tag === false){
      return tag.name
    }
    return `${tag.name} > ${getRecurName(tag.subTag)}`
  }

  return (
    <div className="collectionsInfo">
      <h1>Please select one collection</h1>
      {collections.map(collection =>
        <CollectionCard
          key={collection.id}
          ID={collection.id}
          name={collection.name}
          tag={getRecurName(collection.tags)}
          setDataFromCollection={props.setDataFromCollection}
          masterId={props.masterIdObj[collection.id] || collection.masterAssetId}
        />
      )}
    </div>
  )

} 

export const CollectionCard = props =>  {

  const [masterPath, setMasterPath] = useState("")

  const handleClick = (e) => {
    e.preventDefault()
    let collectionId = props.ID
    getAssetsByCollectionAsync(collectionId).then((data) => {
      props.setDataFromCollection(data)
    })
  }

  useEffect(() => {
    getAssetByIdAsync(props.masterId).then((data) => {
    setMasterPath("/images/" + data.path )
    })
  })

    return (
      <div>
        <p >{props.tag}</p>
        <div className="collection-card">
          <img className="collection-img" src={masterPath} alt={props.name} onClick={handleClick} width="150" height="150" />
          <div className="collection-detail">
            <p className="collection-select" onClick={handleClick}>
              {props.name}
            </p>
          </div>
        </div>
      </div>
    )

}
