import React from "react"
import AssetsInfo from './components/AssetsInfo'
import CollectionsInfo from './components/CollectionsInfo'
import "./styles.css"

export default class App extends React.Component {
  constructor(props){
    super(props);
    this.state={
      assets: [],
      path:"",
      masterID:0
    }
}

  dataFromCollection = (dataFromCollection)=>{
    this.setState({assets:dataFromCollection})
  }

  imgFromMaster = (imgFromMaster)=>{
    this.setState({path:imgFromMaster})
  }

  idFromMaster = (idFromMaster)=>{
    this.setState({masterID:idFromMaster})
  }

  render(){
    return(
      <div className="app">
        <CollectionsInfo dataFromCollection={this.dataFromCollection} masterPath={this.state.path}/>
        <AssetsInfo selectedAssets={this.state.assets} imgFromMaster={this.imgFromMaster} idFromMaster={this.idFromMaster} />
      </div>
    )
  }
}
