import "../styles.css"
import {getCollectionsAsync} from "../api/data.js"




export default class CollectionsInfo extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      collections:"mdmd;smndknsdfgsdg"
    }
    console.log(this.state.collections)
  }

  componentDidMount(){
      getCollectionsAsync().then((data)=>{
        console.dir(data[0])      
        this.setState({
        collections:data  
         })
    }) 
  }


    render(){

      return(
        <div className="collectionsInfo">
          <h1>Please select one collection</h1>
          <CollectionCard />
          {/*
          {this.state.collections.map(collection=>
            <CollectionCard 
              key={collection.id}
              name={collection.name}
            />
          )}
           */}
        </div>
        )
      }
  }

  export class CollectionCard extends React.Component {
    constructor(props) {
      super(props);
    }

    render(){

      return(
          <div className="collection-card">
            <img src="https://via.placeholder.com/150" alt="profile" />
            <div className="collection-detail">
            <p>{this.state.collections}</p>
           {/*
            {this.state.collections.length ? 
                this.state.collections.map((collection)=><p>Name: collection.name</p>)
              : <p>shit2</p>}
          */} 
            </div>
          </div>          
      )

    }
  }
