import React from "react"
import AssetsInfo from './components/AssetsInfo'
import CollectionsInfo from './components/CollectionsInfo'
import "./styles.css"

export default class App extends React.Component {
  constructor(props){
    super(props);
    this.state={
      assets: [],
      path:""
    }
}

  dataFromCollection = (dataFromCollection)=>{
    this.setState({assets:dataFromCollection})
  }

  imgFromMaster = (imgFromMaster)=>{
    this.setState({path:imgFromMaster})
  }

  render(){
    return(
      <div className="app">
        <CollectionsInfo dataFromCollection={this.dataFromCollection}/>
        <AssetsInfo selectedAssets={this.state.assets}/>
      </div>
    )
  }
}
