import React from "react"
import AssetsInfo from './components/AssetsInfo'
import CollectionsInfo from './components/CollectionsInfo'
import "./styles.css"

export default class App extends React.Component {
  constructor(props){
    super(props);
    this.state={
      assets: [],
      path:"",  //path of master img, from A to C
      collectionId:0,  
      lastClickedCollectionId:0,
      defaultMasterId:{},
    }
}

  getDataFromCollection = (dataFromCollection)=>{
    this.setState({assets:dataFromCollection})
  }

  getImgFromMaster = (selectedMasterId, imgFromMaster)=>{
    this.setState({path:imgFromMaster})
    let T=this.state.defaultMasterId
    T[this.state.collectionId]=selectedMasterId
    this.setState({defaultMasterId:T})
  }

  getCollectionId = (collectionId)=>{
    this.setState({collectionId:collectionId})
  }

  setCollectionId = (collectionId)=>{
    this.setState({lastClickedCollectionId:collectionId})
  }

  getDefaultMasterId = (defaultMasterId, collectionId)=> {
    let T=this.state.defaultMasterId
    T[collectionId]=defaultMasterId
    this.setState({defaultMasterId:T})  
  }


  render(){
    //console.log(this.state.defaultMasterId)
    //console.log("xxxx"+this.state.lastClickedCollectionId)
    return(
      <div className="app">
        <CollectionsInfo getDataFromCollection={this.getDataFromCollection}
          getDefaultMasterId={this.getDefaultMasterId} 
          masterPath={this.state.path} 
          collectionId={this.state.collectionId} 
          setCollectionId={this.setCollectionId} />

        <AssetsInfo selectedAssets={this.state.assets}
          defaultMasterId={this.state.defaultMasterId[this.state.lastClickedCollectionId]} 
          getImgFromMaster={this.getImgFromMaster} 
          getCollectionId={this.getCollectionId} />
      </div>
    )
  }
}
