import React from "react"
import "../styles.css"

import { faThumbtack } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";


export default class AssetsInfo extends React.Component {
    render(){
      return(
        <div className="assetsInfo">
          <h1>Assets Info</h1>
          <AssetCard />
          <AssetCard />
          <AssetCard />
          <AssetCard />
        </div>
      )
    }
  }

  export class AssetCard extends React.Component {    
    render(){   
      const [showMaster,setMaster] = useState(false)   
      return(           
          <div className="asset-card">
            <img src="https://via.placeholder.com/150" alt="profile" />
            <div className="asset-detail">
              <p>Name: The Simpsons</p>
              <p>ID: 2</p>
              {showMaster && <FontAwesomeIcon icon={faThumbtack} size="2x" />}
              <button onClick={()=> setMaster(!showMaster)}>
	              Set Master 
              </button>
            </div>
          </div>

      )
    }
  }
