import React from "react"
import AssetsInfo from './components/AssetsInfo'
import CollectionsInfo from './components/CollectionsInfo'
import "./styles.css"

export default class App extends React.Component {
  constructor(props){
    super(props);
    this.state={
      assets: [],
      path:""
    }
}

  dataFromAssets = (dataFromCollection)=>{
    this.setState({assets:dataFromCollection})
  }

  render(){
    return(
      <div className="app">
        <CollectionsInfo dataFromAssets={this.dataFromAssets}/>
        <AssetsInfo selectedAssets={this.state.assets}/>
      </div>
    )
  }
}
